# Regular expression to match codes like BA-02-01, TF2-PY-S13-04, TF-BS-02, etc.
code_pattern = re.compile(r'^[A-Z0-9\-]+')

# Create a list to hold the formatted data
formatted_data = []

# Process each location
for loc in locations:
    id, full_name, name_e, location, direction, latitude, longitude, ip, icon = loc
    
    # Use regex to find the code and the name
    match = code_pattern.match(full_name)
    if match:
        code = match.group(0)
        name = full_name[len(code):].strip()
    else:
        code = ''
        name = full_name

    formatted_data.append([id, code, name, name_e, location, direction, latitude, longitude, ip, icon])

# Create a DataFrame
df = pd.DataFrame(formatted_data, columns=['ID', 'Code', 'Name', 'Name_e', 'Location', 'Direction', 'Latitude', 'Longitude', 'IP', 'Icon'])

# Save to Excel
df.to_excel('locations.xlsx', index=False)






